sp_widget.client_script:
api.controller=function($scope) {
  /* widget controller */
  var c = this;

	$scope.pictureUrl = "	https://s3.us-east-2.amazonaws.com/brainboard-screenshots-prod/architecture/fce28582-6d86-4aaf-b034-f97697f964e8.webp"
	$scope.readMe = "joBROo"

	 c.zoomLevel = 1;

  c.zoomIn = function() {
    c.zoomLevel = Math.min(c.zoomLevel + 0.1, 3);
  };

  c.zoomOut = function() {
    c.zoomLevel = Math.max(c.zoomLevel - 0.1, 0.5);
  };

  c.resetZoom = function() {
    c.zoomLevel = 1;
  };
};

